import logo from './logo.svg';
import './assets/App.css';

function App() {


  return (
    <main>
        <h1> Lista de tareas! </h1>
        <form action="javascript:void(0);">
          <input id="tarea" type="text" name="tarea" placeholder="Descripcion de la tarea" /> 
          <select name="prioridad" id="prioridad">
            <option value="" disabled selected >Elija la Prioridad</option>               
            <option value="prioridad-baja">Baja</option>
            <option value="prioridad-media">Media</option>
            <option value="prioridad-alta">Alta</option>           
          </select> 
          <button id="agregar">Agregar⚡</button>
          <button id="eliminar-seleccionados">Quitar Selecion❌</button>
        </form>
        <h3>Tareas</h3>
        <ul id="lista-tareas">

        </ul>

        <p>Para agregar tareas vamos a tener que hablar de eventos, el DOM y cómo interactuar con él</p>
    </main>
  );
}


export default App;
