import logo from './logo.svg';
import './assets/App.css';
import { useState } from 'react';
import TaskList from './components/TaskList';

function App() {

  /* Declaración de una variable de estado 
     taskList, con su asignador y el valor por defecto vacio */
  const [taskList,setTaskList] = useState([{priority:'prioridad-baja',text:'Pasear al Toby' },
  {priority:'prioridad-media',text:'Hacer ejercicios JS' },
  {priority:'prioridad-alta',text:'Practicar JS' }])

  const [taskName,setTaskName] = useState('Pepito')
  const [priority,setPriority] = useState('')

  function addTask(){
    console.log(taskName)
    setTaskList(taskList.concat({priority: priority, text: taskName}))
  } 

  return (
    <main>
      <h1>Lista de tareas!</h1>

      <form action="javascript:void(0);">
        <input
          id="tarea"
          type="text"
          value={taskName}
          name="tarea"
          onChange={(event) => setTaskName(event.target.value)}
          placeholder="Descripción de la tarea"
        />
        <select name="prioridad" id="prioridad"
          value={priority} 
          onChange={e=> setPriority(e.target.value) }>
            <option value="" disabled selected>Prioridad</option>
            <option value="prioridad-baja">baja</option>
            <option value="prioridad-media">media</option>
            <option value="prioridad-alta">alta</option>
        </select>
        <button id="agregar" onClick={addTask}>Agregar!</button>
      </form>

      <h3>Tareas</h3>
      <TaskList taskList={taskList} />

      <p>
        Para agregar tareas vamos a tener que hablar de eventos, el DOM y cómo
        interactuar con él
      </p>
    </main>
  );
}


export default App;
